function stripeResponseHandler(e,r){var n=$("#payment-form");if(r.error)n.find(".payment-errors").text(r.error.message),n.find("button").prop("disabled",!1);else{var t=r.id;n.append($('<input type="hidden" name="stripeToken" />').val(t)),n.get(0).submit()}}jQuery(function(e){e("#payment-form").submit(function(){var r=e(this);return r.find("button").prop("disabled",!0),Stripe.card.createToken(r,stripeResponseHandler),!1})});